mapper.select:
    	select  
    	bcode, boricode, bno, subrpad(btitle) as btitle, mid, substr(bcontent, 1, 30) as bcontent, to_char(bdate, 'yyyy/mm/dd') as	bdate,
    	bhit, anino, bref, bseq, blvl, bip, bfile, brpt  
    	from (select rownum r_num, a.* from (
		select bcode, boricode, bno, btitle, mid, bcontent, to_char(bdate, 'yyyy/mm/dd') as	bdate,
     	bhit, anino, bref, bseq, blvl, bip, bfile, brpt 
    	from board where bcode=#{bcode}
    	
    				
    					
    						and btitle like '%' || #{searchValue} || '%'
    					
    					
    						and mid like '%' || #{searchValue} || '%'
    					
    					
    						and bcontent like '%' || #{searchValue} || '%'
    					
    				
    		
    	order by bref desc, bseq asc
    	) a )
    	where r_num between #{start} and #{end}
